import { Injectable } from '@angular/core';
interface IModal {
  id: string
  visible: boolean
}
@Injectable({
  providedIn: 'root'
})

export class ModalService {
  private modals: IModal[] = []

  constructor() { }
  register(id: string) {
    this.modals.push({
      id,
      visible: false
    })
  }

  isModalOpen(id: string) {
    return this.modals.find((element) => element.id === id)?.visible
  }
  toggleModal() {
    // this.visible = !this.visible
  }
}
